import { createStyles, MantineNumberSize, MantineColor, MantineTheme } from '@mantine/styles';

export type ActionIconVariant =
  | 'subtle'
  | 'filled'
  | 'outline'
  | 'light'
  | 'default'
  | 'transparent';

export interface ActionIconStylesParams {
  color: MantineColor;
  size: MantineNumberSize;
  radius: MantineNumberSize;
  variant: ActionIconVariant;
}

export const sizes = {
  xs: 18,
  sm: 22,
  md: 28,
  lg: 34,
  xl: 44,
};

interface GetVariantStyles {
  variant: ActionIconVariant;
  theme: MantineTheme;
  color: MantineColor;
}

function getVariantStyles({ variant, theme, color }: GetVariantStyles) {
  if (variant === 'transparent') {
    return {
      border: '1px solid transparent',
      color: theme.fn.variant({ variant: 'subtle', color }).color,
      backgroundColor: 'transparent',
    };
  }

  const colors = theme.fn.variant({ color, variant });

  return {
    backgroundColor: colors.background,
    color: colors.color,
    border: `1px solid ${colors.border}`,
    ...theme.fn.hover({ backgroundColor: colors.hover }),
  };
}

export default createStyles((theme, { color, size, radius, variant }: ActionIconStylesParams) => ({
  root: {
    ...getVariantStyles({ variant, theme, color }),
    position: 'relative',
    height: theme.fn.size({ size, sizes }),
    minHeight: theme.fn.size({ size, sizes }),
    width: theme.fn.size({ size, sizes }),
    minWidth: theme.fn.size({ size, sizes }),
    borderRadius: theme.fn.radius(radius),
    padding: 0,
    lineHeight: 1,
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',

    '&:active': theme.activeStyles,

    '&[data-disabled]': {
      color: theme.colors.gray[theme.colorScheme === 'dark' ? 6 : 4],
      cursor: 'not-allowed',
      backgroundColor:
        variant === 'transparent'
          ? undefined
          : theme.fn.themeColor('gray', theme.colorScheme === 'dark' ? 8 : 1),
      borderColor:
        variant === 'transparent'
          ? undefined
          : theme.fn.themeColor('gray', theme.colorScheme === 'dark' ? 8 : 1),

      '&:active': {
        transform: 'none',
      },
    },

    '&[data-loading]': {
      pointerEvents: 'none',

      '&::before': {
        content: '""',
        position: 'absolute',
        top: -1,
        left: -1,
        right: -1,
        bottom: -1,
        backgroundColor:
          theme.colorScheme === 'dark'
            ? theme.fn.rgba(theme.colors.dark[7], 0.5)
            : 'rgba(255, 255, 255, .5)',
        borderRadius: theme.fn.radius(radius),
        cursor: 'not-allowed',
      },
    },
  },
}));
